import { ref } from 'vue';
import { v4 as uuidv4 } from 'uuid';
export default {
  name: "CustomScriptsInput",
  props: ['modelValue'],
  watch: {
    scripts(value) {
      if (value.length === 0) {
        this.addScript();
      }
      this.$emit('update:modelValue', value);
    }
  },
  setup(props) {
    const scripts = ref(props.modelValue);
    const addScript = () => {
      scripts.value.push({
        id: uuidv4(),
        path: null
      });
    };
    const remove = script => {
      scripts.value = scripts.value.filter(s => s.id !== script.id);
    };
    if (scripts.value.length === 0) {
      addScript();
    }
    return {
      scripts,
      addScript,
      remove
    };
  }
};
