import { useCountries } from "../lib/countries";
export default {
  components: {},
  setup(props, {
    emit
  }) {
    const {
      filteredCountries,
      query
    } = useCountries();
    const selectCountry = country => {
      emit('update:modelValue', country);
      query.value = null;
    };
    return {
      query,
      filteredCountries,
      selectCountry
    };
  }
};
